import 'package:flutter/material.dart';

// --- Placeholder Styles & Data ---
const Color kPrimaryGreen = Color(0xFF4C8C4A);
const Color kLightGreen = Color(0xFFE8F5E9);
const Color kLightChip = Color(0xFFDCEADF); // Very light green for chips

const List<String> recentSearches = [
  'Organic cotton tote',
  'Glass water bottle',
  'Beeswax wraps',
];
const List<String> popularSearches = [
  'Zero-Waste Kits',
  'Compostable Bags',
  'Ethical Fashion',
];

// Reusable data structure for product cards (same as Page 2 but simplified)
class SearchProduct {
  final String title;
  final String subtitle;
  final String imageUrl;
  final List<String> attributes;

  const SearchProduct({
    required this.title,
    required this.subtitle,
    required this.imageUrl,
    required this.attributes,
  });
}

const List<SearchProduct> dummyProducts = [
  SearchProduct(
    title: 'Hydro Flask',
    subtitle: 'Eco Bottle Co.',
    imageUrl:
        'data:image/jpeg;base64,',
    attributes: ['Recycled'],
  ),
  SearchProduct(
    title: 'Shampoo Bar',
    subtitle: 'Green Suds',
    imageUrl:
        'data:image/jpeg;base64,',
    attributes: ['Vegan'],
  ),
  SearchProduct(
    title: 'Bamboo Toothbrush',
    subtitle: 'Brush Naked',
    imageUrl:
        'data:image/jpeg;base64,',
    attributes: ['Compostable'],
  ),
  SearchProduct(
    title: 'Silicone Food Bags',
    subtitle: 'Stasher',
    imageUrl:
        'data:image/jpeg;base64,',
    attributes: ['Zero-Waste'],
  ),
];

// --- Reusable Widget: Search Chip (For recent/popular searches) ---
class _SearchChip extends StatelessWidget {
  final String label;

  const _SearchChip({required this.label});

  @override
  Widget build(BuildContext context) {
    return Padding(
      padding: const EdgeInsets.only(right: 8.0),
      child: ActionChip(
        onPressed: () {},
        label: Text(label, style: const TextStyle(color: Colors.black87)),
        backgroundColor: kLightChip,
        shape: RoundedRectangleBorder(
          borderRadius: BorderRadius.circular(20),
          side: BorderSide.none,
        ),
        padding: const EdgeInsets.symmetric(horizontal: 4, vertical: 4),
      ),
    );
  }
}

// --- Reusable Widget: Small Product Grid Item ---
class _ProductGridItem extends StatelessWidget {
  final SearchProduct product;

  const _ProductGridItem({required this.product});

  @override
  Widget build(BuildContext context) {
    return Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: [
        // Image
        AspectRatio(
          aspectRatio: 1,
          child: ClipRRect(
            borderRadius: BorderRadius.circular(10.0),
            child: Image.network(product.imageUrl, fit: BoxFit.cover),
          ),
        ),
        const SizedBox(height: 8),
        // Title
        Text(
          product.title,
          style: const TextStyle(fontWeight: FontWeight.bold, fontSize: 15),
          maxLines: 1,
          overflow: TextOverflow.ellipsis,
        ),
        // Subtitle
        Text(
          product.subtitle,
          style: const TextStyle(color: Colors.grey, fontSize: 12),
        ),
        const SizedBox(height: 4),
        // Attributes/Tags
        Row(
          children:
              product.attributes.map((attr) {
                return Padding(
                  padding: const EdgeInsets.only(right: 4.0),
                  child: Icon(
                    attr == 'Recycled'
                        ? Icons.recycling
                        : Icons.check_circle_outline,
                    color: kPrimaryGreen,
                    size: 14,
                  ),
                );
              }).toList(),
        ),
      ],
    );
  }
}

// --- The Main Screen Widget ---
class Page05ProductSearch extends StatelessWidget {
  const Page05ProductSearch({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        leading: const BackButton(),
        title: const Text(
          'Find Eco-Friendly Products',
          style: TextStyle(color: kPrimaryGreen, fontWeight: FontWeight.bold),
        ),
      ),
      body: SingleChildScrollView(
        padding: const EdgeInsets.symmetric(horizontal: 16.0),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            // 1. Search Bar
            Padding(
              padding: const EdgeInsets.only(bottom: 24.0),
              child: Container(
                padding: const EdgeInsets.symmetric(horizontal: 16.0),
                decoration: BoxDecoration(
                  color: Colors.grey.shade100,
                  borderRadius: BorderRadius.circular(10.0),
                ),
                child: TextField(
                  decoration: InputDecoration(
                    hintText: 'Search for sustainable goods...',
                    border: InputBorder.none,
                    prefixIcon: Icon(Icons.search, color: Colors.grey.shade600),
                  ),
                ),
              ),
            ),

            // 2. Recent Searches
            const Text(
              'Recent Searches',
              style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
            ),
            const SizedBox(height: 12),
            SizedBox(
              height: 40,
              child: ListView(
                scrollDirection: Axis.horizontal,
                children:
                    recentSearches.map((s) => _SearchChip(label: s)).toList(),
              ),
            ),
            const SizedBox(height: 24),

            // 3. Popular Searches
            const Text(
              'Popular Searches',
              style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold),
            ),
            const SizedBox(height: 12),
            SizedBox(
              height: 40,
              child: ListView(
                scrollDirection: Axis.horizontal,
                children:
                    popularSearches.map((s) => _SearchChip(label: s)).toList(),
              ),
            ),
            const SizedBox(height: 24),

            // 4. Product Grid
            GridView.builder(
              physics:
                  const NeverScrollableScrollPhysics(), // Important for SingleChildScrollView
              shrinkWrap: true,
              gridDelegate: const SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
                crossAxisSpacing: 16.0,
                mainAxisSpacing: 24.0,
                childAspectRatio: 0.7, // Adjusted to fit image and text
              ),
              itemCount: dummyProducts.length,
              itemBuilder: (context, index) {
                return _ProductGridItem(product: dummyProducts[index]);
              },
            ),
            const SizedBox(height: 40),
          ],
        ),
      ),
    );
  }
}
