        ),
        leading: BackButton(onPressed: () => Navigator.pop(context)),
      ),
      body: Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          // 1. Horizontal Filter Tabs
          Padding(
            padding: const EdgeInsets.only(left: 16.0, top: 8.0, bottom: 16.0),
            child: SizedBox(
              height: 40,
              child: ListView(
                scrollDirection: Axis.horizontal,
                children: [
                  _ProductFilterChip(label: 'All', isSelected: true),
                  _ProductFilterChip(label: 'Home', isSelected: false),
                  _ProductFilterChip(label: 'Apparel', isSelected: false),
                  _ProductFilterChip(label: 'Wellness', isSelected: false),
                  _ProductFilterChip(label: 'Food', isSelected: false),
                ],
              ),
            ),
          ),

          // 2. Product Feed List
          Expanded(
            child: ListView(
              padding: const EdgeInsets.symmetric(horizontal: 16.0),
              children: [
                _ProductCard(
                  imageUrl:
                      'data:image/jpeg;base64,',
                  name: 'Bamboo Toothbrush Set',
