                _ProductCard(
                  imageUrl:
                      'data:image/jpeg;base64,',
                  name: 'Kitchen Compost Bin',
                  brand: 'Green Home Solutions',
                  price: '₦15,000',
                  carbonRating: 'B',
                ),
                _ProductCard(
                  imageUrl:
                      'data:image/jpeg;base64,',
                  name: 'Folding Reusable Bag',
